import React, { useState } from 'react';
import { pdfjs } from 'react-pdf';
import './PDFViewer.css';

// ËÆæÁΩÆPDF.js workerÔºà‰ΩøÁî®Êú¨Âú∞Êñá‰ª∂ÔºåÈÅøÂÖçCDNË¢´Êã¶Êà™Ôºâ
pdfjs.GlobalWorkerOptions.workerSrc = '/pdf.worker.min.js';

const PDFViewer = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [useBackup, setUseBackup] = useState(false);

  const onDocumentLoadSuccess = () => {
    setLoading(false);
    setError(null);
  };

  const onDocumentLoadError = (error) => {
    console.error('PDFÂä†ËΩΩÈîôËØØ:', error);
    if (!useBackup) {
      setUseBackup(true);
      setLoading(true);
      setError(null);
    } else {
      setError('PDFÂä†ËΩΩÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•Êñá‰ª∂Ë∑ØÂæÑ„ÄÇÂ¶ÇÊûúÈóÆÈ¢òÊåÅÁª≠Â≠òÂú®ÔºåËØ∑ËÅîÁ≥ªÊäÄÊúØÊîØÊåÅ„ÄÇ');
      setLoading(false);
    }
  };

  // ‰ΩøÁî®ÊµèËßàÂô®ÂÜÖÁΩÆÊü•ÁúãÂô®ÔºåÊó†ÈúÄÈ°µÁ†Å/Áº©ÊîæÊéßÂà∂

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = "/robot-brochure.pdf";
    link.download = "Ôºà1ÔºâÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫(‰∫ßÂìÅÂÆ£‰º†ÂÜå).pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="pdf-viewer">
      <div className="pdf-header">
        <h3>ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫ - ‰∫ßÂìÅÂÆ£‰º†ÂÜå</h3>
        {useBackup && (
          <p className="backup-notice">
            üìÑ Ê≠£Âú®‰ΩøÁî®Â§áÁî®PDFÊñá‰ª∂
          </p>
        )}
        <div className="pdf-controls">
          <div className="zoom-controls">
            <button className="control-btn" onClick={() => window.open(useBackup ? '/sample.pdf' : '/robot-brochure.pdf', '_blank')}>Âú®Êñ∞Á™óÂè£ÊâìÂºÄ</button>
            <button className="control-btn" onClick={handleDownload}>‰∏ãËΩΩPDF</button>
          </div>
        </div>
      </div>

      <div className="pdf-container">
        <div className="pdf-document">
          <iframe
            title="brochure"
            className="pdf-iframe"
            src={(useBackup ? '/sample.pdf' : '/robot-brochure.pdf') + '#view=fitH'}
            onLoad={onDocumentLoadSuccess}
          />
        </div>
      </div>

      <div className="pdf-footer">
        <div className="download-section">
          <button className="download-btn" onClick={handleDownload}>
            <span>üì•</span>
            ‰∏ãËΩΩPDFÊñá‰ª∂
          </button>
          <p className="download-info">
            ÁÇπÂáª‰∏ãËΩΩÂÆåÊï¥ÁöÑ‰∫ßÂìÅÂÆ£‰º†ÂÜåPDFÊñá‰ª∂
          </p>
        </div>
      </div>
    </div>
  );
};

export default PDFViewer;
