import React, { useState } from 'react';
import './ProductInfo.css';

const ProductInfo = () => {
  const features = [
    {
      icon: 'üéØ',
      title: 'Á≤æÂáÜÊµãÈáè',
      description: 'ÈááÁî®ÂÖàËøõÁöÑÊøÄÂÖâÊâ´ÊèèÊäÄÊúØÔºåÂÆûÁé∞ÊØ´Á±≥Á∫ßÁ≤æÂ∫¶ÊµãÈáè'
    },
    {
      icon: 'üõ°Ô∏è',
      title: 'ÂÆâÂÖ®ÂèØÈù†',
      description: 'Â§öÈáçÂÆâÂÖ®‰øùÊä§Êú∫Âà∂ÔºåÁ°Æ‰øùÈößÈÅìÊñΩÂ∑•ÂÆâÂÖ®'
    },
    {
      icon: '‚ö°',
      title: 'È´òÊïà‰Ωú‰∏ö',
      description: 'Ëá™Âä®Âåñ‰Ωú‰∏öÊµÅÁ®ãÔºåÂ§ßÂπÖÊèêÂçáÂ∑•‰ΩúÊïàÁéá'
    },
    {
      icon: 'üìä',
      title: 'Êô∫ËÉΩÂàÜÊûê',
      description: 'AIÁÆóÊ≥ïÂàÜÊûêÔºåÊèê‰æõ‰∏ì‰∏öÁöÑÁõëÊµãÊä•Âëä'
    }
  ];

  const specifications = [
    { label: 'ÊµãÈáèÁ≤æÂ∫¶', value: '¬±1mm' },
    { label: 'Â∑•‰ΩúË∑ùÁ¶ª', value: '0.5-50m' },
    { label: 'Êâ´ÊèèÈÄüÂ∫¶', value: '1000ÁÇπ/Áßí' },
    { label: 'Èò≤Êä§Á≠âÁ∫ß', value: 'IP65' },
    { label: 'Â∑•‰ΩúÊ∏©Â∫¶', value: '-20¬∞C ~ +60¬∞C' },
    { label: 'ÁîµÊ±†Áª≠Ëà™', value: '8Â∞èÊó∂' }
  ];

  const [showImage, setShowImage] = useState(true);
  const [imageSrc, setImageSrc] = useState(`/robot-cover.jpg`);

  const handleImageError = () => {
    if (imageSrc.endsWith('.jpg')) {
      setImageSrc(`/robot-cover.png`);
    } else {
      setShowImage(false);
    }
  };

  return (
    <div className="product-info">
      <div className="hero-section">
        <h2>ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫</h2>
        <p className="hero-description">
          ÈááÁî®ÂÖàËøõÁöÑÊú∫Âô®‰∫∫ÊäÄÊúØÂíåÊô∫ËÉΩÁÆóÊ≥ïÔºå‰∏∫ÈößÈÅìÂ∑•Á®ãÊèê‰æõÂÖ®Êñπ‰ΩçÁöÑÈáèÊµãËß£ÂÜ≥ÊñπÊ°à„ÄÇ
          ÂÖ∑Â§áÈ´òÁ≤æÂ∫¶„ÄÅÈ´òÊïàÁéá„ÄÅÈ´òÂÆâÂÖ®ÊÄßÁöÑÁâπÁÇπÔºåÊòØÁé∞‰ª£ÂåñÈößÈÅìÊñΩÂ∑•ÁöÑÁêÜÊÉ≥ÈÄâÊã©„ÄÇ
        </p>
        {showImage && (
          <div className="hero-image">
            <div className="image-wrapper">
              <img
                src={imageSrc}
                srcSet={`${process.env.PUBLIC_URL}/robot-cover.jpg 1x, ${process.env.PUBLIC_URL}/robot-cover@2x.jpg 2x`}
                alt="ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫Ê≠£ËßÜÂõæ"
                loading="lazy"
                onError={handleImageError}
              />
            </div>
            <p className="image-caption">ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫ÔºàÁ§∫ÊÑèÂõæÔºâ</p>
          </div>
        )}
      </div>

      <div className="features-section">
        <h3>Ê†∏ÂøÉÁâπÊÄß</h3>
        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h4>{feature.title}</h4>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="specifications-section">
        <h3>ÊäÄÊúØËßÑÊ†º</h3>
        <div className="specs-grid">
          {specifications.map((spec, index) => (
            <div key={index} className="spec-item">
              <span className="spec-label">{spec.label}</span>
              <span className="spec-value">{spec.value}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="cta-section">
        <h3>Á´ãÂç≥‰ΩìÈ™å</h3>
        <p>Êü•ÁúãÂÆåÊï¥ÁöÑ‰∫ßÂìÅÂÆ£‰º†ÂÜåÔºå‰∫ÜËß£Êõ¥Â§öÊäÄÊúØÁªÜËäÇÂíåÂ∫îÁî®Ê°à‰æã</p>
        <button className="cta-button">
          <span>üìÑ</span>
          Êü•ÁúãÂÆ£‰º†ÂÜå
        </button>
      </div>
    </div>
  );
};

export default ProductInfo;
