import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import './PDFViewer.css';

// ËÆæÁΩÆPDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFViewer = () => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [useBackup, setUseBackup] = useState(false);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    setLoading(false);
    setError(null);
  };

  const onDocumentLoadError = (error) => {
    setError('PDFÂä†ËΩΩÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•Êñá‰ª∂Ë∑ØÂæÑ„ÄÇÂ¶ÇÊûúÈóÆÈ¢òÊåÅÁª≠Â≠òÂú®ÔºåËØ∑ËÅîÁ≥ªÊäÄÊúØÊîØÊåÅ„ÄÇ');
    setLoading(false);
    console.error('PDFÂä†ËΩΩÈîôËØØ:', error);
  };

  const goToPrevPage = () => {
    setPageNumber(prev => Math.max(prev - 1, 1));
  };

  const goToNextPage = () => {
    setPageNumber(prev => Math.min(prev + 1, numPages));
  };

  const zoomIn = () => {
    setScale(prev => Math.min(prev + 0.2, 3.0));
  };

  const zoomOut = () => {
    setScale(prev => Math.max(prev - 0.2, 0.5));
  };

  const resetZoom = () => {
    setScale(1.0);
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = "/robot-brochure.pdf";
    link.download = "ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫‰∫ßÂìÅÂÆ£‰º†ÂÜå.pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="pdf-viewer">
      <div className="pdf-header">
        <h3>ÈößÈÅìÁªºÂêàÈáèÊµãÊú∫Âô®‰∫∫ - ‰∫ßÂìÅÂÆ£‰º†ÂÜå</h3>
        <div className="pdf-controls">
          <div className="page-controls">
            <button 
              onClick={goToPrevPage} 
              disabled={pageNumber <= 1}
              className="control-btn"
            >
              ‚Üê ‰∏ä‰∏ÄÈ°µ
            </button>
            <span className="page-info">
              {pageNumber} / {numPages || '--'}
            </span>
            <button 
              onClick={goToNextPage} 
              disabled={pageNumber >= numPages}
              className="control-btn"
            >
              ‰∏ã‰∏ÄÈ°µ ‚Üí
            </button>
          </div>
          
          <div className="zoom-controls">
            <button onClick={zoomOut} className="control-btn">üîç-</button>
            <span className="zoom-info">{Math.round(scale * 100)}%</span>
            <button onClick={zoomIn} className="control-btn">üîç+</button>
            <button onClick={resetZoom} className="control-btn">ÈáçÁΩÆ</button>
          </div>
        </div>
      </div>

      <div className="pdf-container">
        {loading && (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Ê≠£Âú®Âä†ËΩΩPDFÊñá‰ª∂...</p>
          </div>
        )}

        {error && (
          <div className="error-container">
            <div className="error-icon">‚ö†Ô∏è</div>
            <h4>PDFÂä†ËΩΩÂ§±Ë¥•</h4>
            <p>{error}</p>
            <div className="error-suggestions">
              <p>ËØ∑Â∞ùËØï‰ª•‰∏ãËß£ÂÜ≥ÊñπÊ°àÔºö</p>
              <ul>
                <li>Ê£ÄÊü•PDFÊñá‰ª∂ÊòØÂê¶Â≠òÂú®</li>
                <li>Á°Æ‰øùÊñá‰ª∂Ë∑ØÂæÑÊ≠£Á°Æ</li>
                <li>Ê£ÄÊü•Êñá‰ª∂ÊòØÂê¶ÊçüÂùè</li>
                <li>Â∞ùËØïÂà∑Êñ∞È°µÈù¢</li>
              </ul>
            </div>
          </div>
        )}

        {!loading && !error && (
          <div className="pdf-document">
            <Document
              file="/robot-brochure.pdf"
              onLoadSuccess={onDocumentLoadSuccess}
              onLoadError={onDocumentLoadError}
              loading={
                <div className="loading-container">
                  <div className="loading-spinner"></div>
                  <p>Ê≠£Âú®Âä†ËΩΩPDFÊñá‰ª∂...</p>
                </div>
              }
              error={
                <div className="error-container">
                  <div className="error-icon">‚ö†Ô∏è</div>
                  <h4>PDFÊñá‰ª∂Êó†Ê≥ïÂä†ËΩΩ</h4>
                  <p>ËØ∑Ê£ÄÊü•Êñá‰ª∂ÊòØÂê¶Â≠òÂú®ÊàñÂ∞ùËØïÂà∑Êñ∞È°µÈù¢</p>
                  <button 
                    className="retry-btn"
                    onClick={() => window.location.reload()}
                  >
                    Âà∑Êñ∞È°µÈù¢
                  </button>
                </div>
              }
            >
              <Page 
                pageNumber={pageNumber} 
                scale={scale}
                renderTextLayer={false}
                renderAnnotationLayer={false}
              />
            </Document>
          </div>
        )}
      </div>

      <div className="pdf-footer">
        <div className="download-section">
          <button className="download-btn" onClick={handleDownload}>
            <span>üì•</span>
            ‰∏ãËΩΩPDFÊñá‰ª∂
          </button>
          <p className="download-info">
            ÁÇπÂáª‰∏ãËΩΩÂÆåÊï¥ÁöÑ‰∫ßÂìÅÂÆ£‰º†ÂÜåPDFÊñá‰ª∂
          </p>
        </div>
      </div>
    </div>
  );
};

export default PDFViewer;
