import React, { useState } from 'react';
import './ProductInfo.css';

const ProductInfo = () => {
  const features = [
    {
      icon: '🎯',
      title: '精准测量',
      description: '采用先进的激光扫描技术，实现毫米级精度测量'
    },
    {
      icon: '🛡️',
      title: '安全可靠',
      description: '多重安全保护机制，确保隧道施工安全'
    },
    {
      icon: '⚡',
      title: '高效作业',
      description: '自动化作业流程，大幅提升工作效率'
    },
    {
      icon: '📊',
      title: '智能分析',
      description: 'AI算法分析，提供专业的监测报告'
    }
  ];

  const specifications = [
    { label: '测量精度', value: '±1mm' },
    { label: '工作距离', value: '0.5-50m' },
    { label: '扫描速度', value: '1000点/秒' },
    { label: '防护等级', value: 'IP65' },
    { label: '工作温度', value: '-20°C ~ +60°C' },
    { label: '电池续航', value: '8小时' }
  ];

  const [showImage, setShowImage] = useState(true);
  const [imageSrc, setImageSrc] = useState('/robot-cover.jpg');

  const handleImageError = () => {
    setShowImage(false);
  };

  return (
    <div className="product-info">
      <div className="hero-section">
        <h2>隧道综合量测机器人</h2>
        <p className="hero-description">
          采用先进的机器人技术和智能算法，为隧道工程提供全方位的量测解决方案。
          具备高精度、高效率、高安全性的特点，是现代化隧道施工的理想选择。
        </p>
        {showImage && (
          <div className="hero-image">
            <div className="image-wrapper">
              <img
                src={imageSrc}
                alt="隧道综合量测机器人正视图"
                loading="lazy"
                onError={handleImageError}
              />
            </div>
            <p className="image-caption">隧道综合量测机器人（示意图）</p>
          </div>
        )}
      </div>

      <div className="features-section">
        <h3>核心特性</h3>
        <div className="features-grid">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <h4>{feature.title}</h4>
              <p>{feature.description}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="specifications-section">
        <h3>技术规格</h3>
        <div className="specs-grid">
          {specifications.map((spec, index) => (
            <div key={index} className="spec-item">
              <span className="spec-label">{spec.label}</span>
              <span className="spec-value">{spec.value}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="cta-section">
        <h3>立即体验</h3>
        <p>查看完整的产品宣传册，了解更多技术细节和应用案例</p>
        <button className="cta-button">
          <span>📄</span>
          查看宣传册
        </button>
      </div>
    </div>
  );
};

export default ProductInfo;
